html.html--pdf section h3 { color: transparent; text-shadow: 0 0 white; }

/* theme/adventurer/high-contrast */
html .page * { text-shadow: 0 0 0 #505050; color: transparent; }

html .page h2, html .page h3, html .page h4, html .page h5, html .page h6 { text-shadow: 0 0 0 #505050; color: transparent; }

html .page h3 { background: transparent; border-bottom: 1.5pt solid #505050; text-shadow: none !important; margin-top: 3pt; color: #505050 !important; }

html .page label, html .page p, html .page a { text-shadow: 0 0 0 #505050; color: transparent; }

html .page h5:first-line { text-shadow: 0 0 0 #404040; color: transparent; }

html .page .field__control--control_checkbox input[type=checkbox] + label, html .page .field--control_checkgrid .field__control input[type=checkbox] + label { border-color: #404040; }

html .page .field__control u { display: none; }

