units.stylesheet:
/* core */
.align_left { text-align: left; }

.align_left label, .align_left input, .align_left output { text-align: left !important; }

.align_right { text-align: right; }

.align_right label, .align_right input, .align_right output { text-align: right !important; }

.align_center { text-align: center; }

.align_center label, .align_center input, .align_center output { text-align: center !important; }

.blk { margin: 0; }

.blk:before, .blk:after { content: ""; display: block; overflow: hidden; flex: 2 10 0.2rem; width: 0.2rem; height: 0.2rem; }

.blk + .blk:before { display: none; }

.blk > .blk:first-child:before, .blk > .blk:last-child:after, .blk .inner > .blk:first-child:before, .blk .inner > .blk:last-child:after { display: none; }

.unblk > .blk:before, .unblk > .blk:after, .unblk > .inner > .blk:before, .unblk > .inner > .blk:after { flex-basis: 0; }

.unblk.layout > * > .blk:first-child:before, .unblk.layout > * > .blk:last-child:after { flex-basis: 0; }

.row.blk .row.blk:before, .row.blk .row.blk:after { display: none; }

/* Calculations */
.row.row--calc > .row__inner { align-items: flex-end; }

