* { -webkit-print-color-adjust: exact !important; color-adjust: exact !important; }

body { margin: 0; }

.skip-link { display: none; }

*::selection { background: --c-accent; text-shadow: 0 0 0 white; color: transparent; }

*::-moz-selection { background: --c-accent; text-shadow: 0 0 0 white; color: transparent; }

/* Fields */
.field { display: flex; flex-direction: row; position: relative; flex: 1 1 auto; padding: 0; break-inside: avoid; /* width and flex */ width: 2.5rem; flex: 3 1 auto; /* size = */ /* align= */ /* control= */ }

