.field.size_gargantuan .field__control { height: 2.25rem; }

.field.size_gargantuan input { font-size: 1.5rem; height: 2.25rem; line-height: 2.25rem; }

.field label, .field input { text-align: center; }

.field.align_left label, .field.align_left input { text-align: left; }

.field.align_centre label, .field.align_centre input { text-align: center; }

.field.align_right label, .field.align_right input { text-align: right; }

.field--control_checkbox, .field--control_radio { width: 1rem; flex: 0 0 auto; }

ul > .field--control_checkbox, ul > .field--control_radio { margin-left: -1.3rem; margin-top: 0.1rem; margin-bottom: 0.1rem; }

.field.field--control_compound, .field.field--control_progression { width: auto; }

.field__frame { display: flex; flex-direction: column; justify-content: flex-end; flex: 1 1 auto; max-width: 100%; }

.field__box { flex: 0 1 auto; display: flex; flex-direction: row; align-items: flex-end; }

.field__boxes { display: flex; flex-direction: row; align-items: flex-end; }

.field__boxes .field__boxes__inner { position: relative; }

.field__control { display: flex; flex-direction: row; flex: 1 1 auto; }

.field__control input { flex: 1 1 auto; border: 0 none; }

table td .field { margin-left: 0.2rem; margin-right: 0.2rem; }

.field { /* frame= */ }

.field--frame_none { padding: 0; }

.field--frame_annotation { padding: 0; position: relative; }

.field--frame_annotation label.field__annotation { position: absolute; top: 0; right: 0; font-size: 0.7rem; text-align: right !important; }

.field.field--frame_left, .field.field--frame_right { padding-top: 0; width: auto; }

.field.field--frame_left .field__frame, .field.field--frame_right .field__frame { flex-direction: row; justify-content: flex-end; }

.field.field--frame_left .field__frame legend, .field.field--frame_left .field__frame > label, .field.field--frame_right .field__frame legend, .field.field--frame_right .field__frame > label { flex: 10 1 auto; min-height: 1.1rem; line-height: 1.1rem; padding: 0.25rem 0.3rem 0; }

.field.field--frame_left .field__frame > label, .field.field--frame_right .field__frame > label { font-size: 1rem; }

