.layout--layout_indent-lrw > .g:first-child .field--control_checkbox, .layout--layout_indent-lrw > .g:first-child .level-marker, .layout--layout_indent-lrw > .g:last-child .field--control_checkbox, .layout--layout_indent-lrw > .g:last-child .level-marker { margin-left: auto; margin-right: auto; }

.layout--layout_spells-list { grid-template-columns: 1fr 2rem 1fr; grid-template-areas: "a level b"; align-items: center; }

.layout--layout_spells-list .level { grid-area: "level"; }

.layout--flex > * { display: flex; flex-direction: column; flex-wrap: nowrap; }

.layout .layout__place.loc_top-left { grid-area: "top-left"; }

