units.stylesheet:
/* theme/adventurer/high-contrast */
html .page * { text-shadow: 0 0 0 #505050; color: transparent; }

html .page h2, html .page h3, html .page h4, html .page h5, html .page h6 { text-shadow: 0 0 0 #505050; color: transparent; }

html .page h3 { background: transparent; border-bottom: 1.5pt solid #505050; text-shadow: none !important; margin-top: 3pt; }

html .page label, html .page p, html .page a { text-shadow: 0 0 0 #505050; color: transparent; }

html .page h5:first-line { text-shadow: 0 0 0 #404040; color: transparent; }

html .page .field__control--control_checkbox input[type=checkbox] + label, html .page .field--control_checkgrid .field__control input[type=checkbox] + label { border-color: #404040; }

html .page .field__control u { display: none; }

/* theme/adventurer */
/* Pathfinder style */
html, a, p, * { text-shadow: 0 0 0 #787878; color: transparent; }

.copyright-attribution, .copyright-attribution a { text-shadow: 0 0 0 #909090; color: transparent; }

article header h6 { border-bottom: 0.05rem solid #b0b0b0; }

.field .field__inner { border-color: #787878 !important; }

.field.field--misc .field__inner { border-color: #b0b0b0 !important; }

.field .underlay { text-shadow: 0 0 0 #505050; color: transparent; opacity: 0.2; }

.field.field--misc label, .label--misc { text-shadow: 0 0 0 #b0b0b0; color: transparent; }

label.field--alignment__good, label.field--alignment__evil, label.field--alignment__lawful, label.field--alignment__chaotic { text-shadow: 0 0 0 #505050; color: transparent; }

.shade, blockquote { background: rgba(144, 144, 144, 0.1); }

.field--zebra input:nth-child(2n + 1) { background: rgba(144, 144, 144, 0.1); }

.list--zebra > *:nth-child(2n) { background: rgba(144, 144, 144, 0.1); }

.list--zebra + .list--zebra > *:nth-child(2n) { background: none; }

.list--zebra + .list--zebra > *:nth-child(2n + 1) { background: rgba(144, 144, 144, 0.1); }

.list--zebra.list--zebra-inverse > *:nth-child(2n) { background: none; }

.list--zebra.list--zebra-inverse > *:nth-child(2n + 1) { background: rgba(144, 144, 144, 0.1); }

.table--zebra tbody tr:nth-child(2n) td, .table--zebra tbody tr:nth-child(2n) th { background: rgba(144, 144, 144, 0.1); }

.table--zebra tbody tr:nth-child(2n) td.shade, .table--zebra tbody tr:nth-child(2n) th.shade { background: rgba(144, 144, 144, 0.2); }

table tr.hr td, table tr.hr th { border-top-color: #787878; }

@font-face { font-family: "Roboto Condensed"; src: url('data:application/x-font-woff;charset=utf-8;base64,'); }

@font-face { font-family: "Merriweather"; font-weight: 900; src: url('data:application/x-font-woff;charset=utf-8;base64,'); }

@font-face { font-family: "Merriweather"; font-weight: 400; font-style: italic; src: url('data:application/x-font-woff;charset=utf-8;base64,'); }

/* Fonts */
body, p, input, output { font-family: "Roboto Condensed", "Arial Narrow", Arial, sans-serif; }

h1, h2, h3, h5, legend, article header .annotation, .field u, .level-marker__level, .page__watermark__inner, .span--value { font-family: "Merriweather", Georgia, "Times New Roman", Times, serif; font-weight: 900; }

h4 { font-family: "Merriweather", Georgia, "Times New Roman", Times, serif; font-weight: 400; font-style: italic; }

article h6 { font-family: "Roboto Condensed", "Arial Narrow", Arial, sans-serif; font-weight: bold; }

p.p--flags span { background: #909090; text-shadow: 0 0 0 #ffffff; color: transparent; border-radius: 0.2rem; }

p.p--flags.blk:before { display: none; }

.blk + p.p--flags { margin-top: -0.2rem; }

/* Column Borders */
.column.column--non-final > * { padding-bottom: 0 !important; border-bottom: 0 none !important; }

/* Headings */
.class-name { margin-bottom: 1rem; }

.class-name h2, .class-name h5 { text-align: center; text-shadow: 0 0 0 #707070; color: transparent; margin: 0.3rem 0; }

h1, h2, h3, h5 { text-transform: uppercase; }

h2 { text-align: center; padding: 0; flex: 0 1 auto; font-size: 1.6rem; }

h3 { font-size: 0.92rem; height: 1.26rem; line-height: 1.38rem; font-kerning: normal; letter-spacing: 0.5pt; margin: 0; text-align: center; background-image: url('data:image/svg+xml,<svg width="auto" height="auto" xmlns="http://www.w3.org/2000/svg"><svg x="0" y="0" width="51%" height="30" viewBox="0 0 2000 30" preserveAspectRatio="xMinYMid slice"><path fill="%23808080" d="M2000,30V0H12.074C8.74,4.375,0,0,0,0s4.334,8.75,0,12.164V30H2000z"/><path fill="%23FFFFFF" d="M6.896,10.28C7.448,9.729,6.71,6.785,5.79,5.863c0.921,0.922,3.866,1.66,4.418,1.107 c-0.552,0.553,0.188,3.498,1.108,4.42C10.395,10.469,7.448,9.729,6.896,10.28z"/></svg><svg x="49%" y="0" width="51%" height="30" viewBox="0 0 2000 30" preserveAspectRatio="xMaxYMid slice"><path fill="%23808080" d="M0,30V0h1987.926C1991.26,4.375,2000,0,2000,0s-4.334,8.75,0,12.164V30H0z"/><path fill="%23FFFFFF" d="M1993.104,10.28c-0.553-0.551,0.185-3.496,1.106-4.417c-0.922,0.921-3.867,1.659-4.418,1.107 c0.551,0.552-0.188,3.498-1.109,4.419C1989.605,10.469,1992.552,9.729,1993.104,10.28z"/></svg></svg>'); background-position: top; background-repeat: no-repeat; background-size: 100% 1.28rem; color: transparent; text-shadow: 0 0 white, 0 0 0.2rem #505050, 0 0.1rem 0.1rem #505050; }

h3:first-letter { font-size: 0.98rem; }

hr.hr--tail { border-top: 0 none; height: 0.5rem; margin-top: -0.2rem; background-image: url('data:image/svg+xml,<svg width="auto" height="auto" xmlns="http://www.w3.org/2000/svg"><svg x="0" y="0" width="51%" height="4.6" viewBox="0 0 2000 4.6" preserveAspectRatio="xMinYMid slice"><path fill="%23a0a0a0" d="M2000.013,4.589H12.073C8.739,0.214,0,4.589,0,4.589l2.697-2.645c0,0,8.708-4.375,12.043,0h1985.272V4.589z"/></svg><svg x="49%" y="0" width="51%" height="4.6" viewBox="0 0 2000 4.6" preserveAspectRatio="xMaxYMid slice"><path fill="%23a0a0a0" d="M0,4.589h1987.939c3.334-4.375,12.073,0,12.073,0l-2.697-2.645c0,0-8.708-4.375-12.043,0H0V4.589z"/></svg></svg>'); background-position: top; background-repeat: no-repeat; background-size: 100% 1.3rem; }

h4 { text-shadow: 0 0 0 --c-accent; color: transparent; font-size: 2rem; line-height: 2.6rem; }

section.section--primary h4 { padding-left: 0.4rem; }

h6 { font-size: 1rem; font-weight: normal; text-align: left; margin: 0; line-height: 1.4rem; }

h6.h6--fade { text-shadow: 0 0 0 #909090; color: transparent; opacity: 0.7; }

legend, h5 { text-shadow: 0 0 0 #606060; color: transparent; font-size: 0.8rem; text-transform: uppercase; }

section.section--primary { margin-bottom: 0.2rem; }

section.section--primary > .section__inner { padding: 0 0.4rem 0.2rem 0.4rem; border: 1.5pt solid #808080; border-top: 0 none; background: rgba(255, 255, 255, 0.5); }

section.section--primary > .section__inner hr { margin-left: -0.4rem; margin-right: -0.4rem; }

section.section--primary > .section__inner > .list { margin-left: -0.4rem; margin-right: -0.4rem; margin-bottom: -0.2rem; }

section.section--primary > .section__inner > .list.list--hr > * { border-bottom-color: #808080; }

section.section--primary > .section__inner > .list > .g { padding-left: 0.4rem; padding-right: 0.4rem; }

span.vr { border-left: 0.2rem solid #b0b0b0; height: 2.5rem !important; }

span.span--article-cat { font-weight: bold; text-transform: uppercase; text-align: right; }

span.span--article-cat, article header .row > .row__inner > span.span--article-cat { font-family: "Roboto Condensed", "Arial Narrow", Arial, sans-serif; }

.page-build h4 { margin-top: 0; }

.page__number { color: transparent; text-shadow: 0 0 0 #303030, 0 0 0.5rem white, 0 0 0.2rem white, 0.1rem 0.1rem 0.1rem white, -0.1rem 0.1rem 0.1rem white, 0rem 0.1rem white, -0.2rem 0.1rem white; }

/* Icons */
