/* Classes */
.class-icon.icon_box { width: 6rem; height: 6rem; margin-left: auto; margin-right: auto; flex: 0 0 auto; border: 1px solid #b0b0b0; background: rgba(255, 255, 255, 0.5); }

.field input { text-shadow: 0 0 0 #505050; color: transparent; background: none; }

.field.field--temp .field__box { border-bottom: 0.2rem solid #b0b0b0; margin-bottom: -0.2rem; }

.field.field--temp .field__box.field__box--border_full { border: 0.2rem solid #b0b0b0; margin: -0.2rem 0; border-radius: 0.4rem; outline-radius: 0.4rem; -moz-outline-radius: 0.4rem; }

.field.field--temp.field--icon .field__box.field__box--border_full { border-left: 0 none; margin-left: 0; }

.field.field--border_circle { width: 3.1rem; flex: 0 0 auto; position: relative; }

.field.field--border_circle .field__frame { width: 3.1rem; }

.field.field--border_circle .field__box, .field.field--border_circle .field__box.field__box--border_full { width: 2.7rem; height: 2.7rem; border-radius: 2.7rem; border-style: solid; border-width: 0.2rem; border-color: #b0b0b0; margin: -0.2rem 0; background: white; }

.field.field--border_circle .field__control { width: 2.7rem; height: 2.7rem; }

.field.field--border_circle .field__control u { height: 2.7rem; line-height: 2.7rem; }

.field.field--border_circle input { width: 2.7rem !important; height: 2.7rem !important; line-height: 2.7rem !important; text-align: center; padding: 0; margin: 0; }

.field.field--border_circle .field__overlay { position: absolute; right: -0.3rem; bottom: -0.2rem; padding: 0.1rem 0 0 0.1rem; width: 1.3rem; height: 1.3rem; line-height: 1.3rem; border-radius: 1rem; background: white; border-style: solid; border-width: 0 0.2rem 0.2rem 0; border-color: #b0b0b0; font-size: 1.2rem; text-align: center; }

table.table--collapse .field { margin-top: -0.038rem; margin-bottom: -0.038rem; }

.field__boxes .field__box.field__box--border_full { border-radius: 0; border-right-width: 0; border-right-style: none; }

.field__boxes .field__boxes__inner .field__box--temp { border-width: 0.2rem; border-style: solid; border-color: #b0b0b0; }

.field__boxes .field__boxes__inner .field__box--temp .field__control, .field__boxes .field__boxes__inner .field__box--temp .field__overlay { margin-top: -0.1rem; margin-bottom: -0.15rem; }

.field__boxes .field__boxes__inner:first-child .field__box.field__box--border_full { border-top-left-radius: 0.2rem; border-bottom-left-radius: 0.2rem; }

.field__boxes .field__boxes__inner:last-child .field__box.field__box--border_full { border-top-right-radius: 0.2rem; border-bottom-right-radius: 0.2rem; border-right-width: 0.075rem; border-right-style: solid; }

.field__boxes .field__boxes__inner:last-child .field__box.field__box--border_full.field__box--temp { border-top-right-radius: 0.4rem; border-bottom-right-radius: 0.4rem; border-right-width: 0.2rem; border-right-style: solid; border-right-color: #b0b0b0; }

.field__box { border-bottom: 0.04rem solid #787878; margin-bottom: -0.04rem; }

.field--icon .field__box, .field__box.field__box--border_full { border-radius: 0.2rem; outline-radius: 0.2rem; -moz-outline-radius: 0.2rem; }

.field__box.field__box--border_full { border: 0.075rem solid #787878; margin: -0.075rem 0; background: white; }

.field--icon .field__box.field__box--border_full { border-left-width: 0; margin-left: 0; }

.field__box.field__box--border_bottom-right { border-bottom: 0.04rem solid #787878; border-right: 0.04rem solid #787878; margin-bottom: -0.04rem; }

td:last-child .field__box.field__box--border_bottom-right { border-right: 0 none; }

.field__box.field__box--border_right { border-bottom: 0 none; margin-bottom: 0; border-right: 0.04rem solid #787878; margin-right: -0.04rem; }

td:last-child .field__box.field__box--border_right { border-right: 0 none; margin-right: 0; }

.field--ref .field__box { border-bottom: 0.075rem dashed #787878; margin-bottom: -0.075rem; }

.field--ref .field__box.field__box--border_full { border: 0.075rem dashed #787878; margin: -0.75 .field--ref .field__box.field__box--border_full 0.1rem 0; }

.field__box.field__box--border_none { border: 0 none; margin: 0; }

article header .field__box { border-bottom-width: 0.05rem; margin-bottom: -0.05rem; }

.field__control u { font-size: 1rem; font-family: "Merriweather", Georgia, "Times New Roman", Times, serif; font-weight: 900; text-shadow: 0 0 0 #787878; color: transparent; opacity: 0.2; }

